import React, { useState }from 'react'
import { Indicator } from '../components/UI/indicator'
import { VariantWrapper } from '../components/UI/variantWrapper'
import { AppButton } from '../components/UI/AppButton'
import { Heading } from '../typography/heading'

const StepTwo = () => {
  const [checkedAnswer, setCheckedAnswer] = useState(null)

  const handleCheck = (answerId) => {
    setCheckedAnswer(answerId)
  }
  const options = [
    {
      id: Math.floor(Math.random() * 1000),
      text: 'Ваш ответ 1',
    },
    {
      id: Math.floor(Math.random() * 1000),
      text: 'Ваш ответ 2',
    },
    {
      id: Math.floor(Math.random() * 1000),
      text: 'Ваш ответ 3',
    },
    {
      id: Math.floor(Math.random() * 1000),
      text: 'Ваш ответ 4',
    },
  ]
  return (
    <div className="container">
      <div className="wrapper">
        <div className="variants-quiz">
          <Indicator isActiveFirst=" _active" percent="15%" />
          <div className="question">
            <Heading headingType="h2 " text="1. Занимательный вопрос" />
            <ul className="variants">
              {options.map((element) => (
                <VariantWrapper
                  key={element.id}
                  id={element.id}
                  labelText={element.text}
                />
              ))}
            </ul>
            <AppButton disabled={true} id="next-btn" text="Далее" />
          </div>
        </div>
      </div>
    </div>
  )
}

export default StepTwo
