import React, { useState }from 'react'
import { Indicator } from '../components/UI/indicator'
import { VariantWrapper } from '../components/UI/variantWrapper'
import { Heading } from '../typography/heading'
import { AppButton } from '../components/UI/AppButton'
import { ImgVariantWrapper } from '../components/UI/imgVariantWrapper'

const StepThree = () => {
  const [checkedAnswer, setCheckedAnswer] = useState(null)

  const handleCheck = (answerId) => {
    setCheckedAnswer(answerId)
  }
  const options = [
    {
      id: "answer-option-1",
      text: 'Ваш ответ 1',
      src: "./img/laugh.png"
    },
    {
      id: "answer-option-2",
      text: 'Ваш ответ 2',
      src: "./img/hearts.png"
    },
    {
      id: "answer-option-3",
      text: 'Ваш ответ 3',
      src: "./img/smirk.png"
    },
    {
      id: "answer-option-4",
      text: 'Ваш ответ 4',
      src: "./img/fright.png"
    },
  ]
  return (
    <div className="container">
      <div className="wrapper">
        <div className="emoji-quiz">
          <Indicator
            isActiveFirst=" _active"
            isActiveSecond=" _active"
            percent="30%"
          />
          <div className="question">
            <Heading headingType="h2" text="3. Занимательный вопрос" />
            <ul className="emoji-variants">
              {options.map((element) => (
                <ImgVariantWrapper
                  key={element.id}
                  id={element.id}
                  labelText={element.text}
                  src={element.src}
                  checked={checkedAnswer === element.id}
                  onChange={() => handleCheck(element.id)}
                />
              ))}
            </ul>
            <AppButton disabled={true} id="next-btn" text="Далее" />
          </div>
        </div>
      </div>
    </div>
  )
}

export default StepThree
