import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

import { Heading } from '../components/typography/heading'
import { AppInput } from '../components/UI/appInput'
import { AppButton } from '../components/UI/AppButton'
import { useTheme } from '../hooks/useTheme'

const Welcome = () => {
  // переключалка тем
  const { theme, setTheme } = useTheme()

  function handleDarkThemeClick() {
    setTheme('dark')
  }

  function handleLightThemeClick() {
    setTheme('light')
  }

  function handleCheckboxChange() {
    var themeCheckbox = document.getElementById('themeCheckbox')

    if (themeCheckbox.checked) {
      handleDarkThemeClick() // Вызываем функцию для активного состояния
    } else {
      handleLightThemeClick() // Вызываем функцию для неактивного состояния
    }
  }

  const navigate = useNavigate()

  const [name, setName] = useState(null)
  const [number, setNumber] = useState(null)

  const isButtonDisabled = !(name && number)

  const handleNameInput = (e) => {
    setName(e.target.value)
  }
  const handleNumberInput = (e) => {
    setNumber(e.target.value)
  }

  return (
    <div>
      <div className='container'>
        <div className='wrapper'>
          <div className='welcome'>
            <input
              type='checkbox'
              id='themeCheckbox'
              onChange='handleCheckboxChange()'
            />
            <Heading
              headingType='h1'
              text='Добро пожаловать в квиз от лучшего учебного центра '
            />
            <div className='welcome__form'>
              <AppInput
                required={true}
                type='text'
                name='username'
                id='username'
                placeholder='Ваш ответ'
                labelText='Ваше имя'
                onInput={handleNameInput}
              />
              <label className='input-wrapper' htmlFor='username'>
                <AppInput
                  required={true}
                  labelText='Ваш номер'
                  type='tel'
                  name='phone'
                  id='phone'
                  placeholder='+998 9- --- -- -- '
                  pattern='^(?:\+998)?(?:\d{2})?(?:\d{7})$'
                  errText='Введите номер в правильном формате'
                  onInput={handleNumberInput}
                />
                <AppButton
                  disabled={isButtonDisabled}
                  onClick={() => navigate('/step-one')}
                  text='Далее'
                />
              </label>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Welcome
