import React, { useState }from 'react'
import { Indicator } from '../components/UI/indicator'
import { VariantWrapper } from '../components/UI/variantWrapper'
import { AppButton } from '../components/UI/AppButton'
import { Heading } from '../typography/heading'


const StepTwo = (onStepChange) => {
    const [answer, setAnswer] = useState(null)

  const isButtonDisabled = !answer

  const handleAnswerInput = (e) => {
    setAnswer(e.target.value)
  }

  const [checkedAnswer, setCheckedAnswer] = useState(null)

  const handleCheck = (answerId) => {
    setCheckedAnswer(answerId)
  }
  const options = [
    {
      id: "answer-option-1",
      text: 'Ваш ответ 1',
    },
    {
      id: "answer-option-2",
      text: 'Ваш ответ 2',
    },
    {
      id: "answer-option-3",
      text: 'Ваш ответ 3',
    },
    {
      id: "answer-option-4",
      text: 'Ваш ответ 4',
    },
  ]
  return (
    <div className="container">
      <div className="wrapper">
        <div className="variants-quiz">
          <Indicator isActiveFirst=" _active" percent="15%" />
          <div className="question">
            <Heading headingType="h2 " text="1. Занимательный вопрос" />
            <ul className="variants">
              {options.map((element) => (
                <VariantWrapper
                  key={element.id}
                  id={element.id}
                  labelText={element.text}
                  checked={checkedAnswer === element.id}
                  onChange ={() => handleCheck(element.id)}
                />
              ))}
            </ul>
            <AppButton disabled={!checkedAnswer} id="next-btn" text="Далее" onClick={() => onStepChange()} />
          </div>
        </div>
      </div>
    </div>
  )
}

export default StepTwo
