export const ThemeSwitcher = () => {
  const { theme, setTheme } = useTheme()

  function handleDarkThemeClick() {
    setTheme('dark')
  }

  function handleLightThemeClick() {
    setTheme('light')
  }

  return (
    <div className='themeSwitcher'>
      <button class='themeButton' onClick={handleDarkThemeClick}>
        Dark
      </button>
      <button class='themeButton' onClick={handleLightThemeClick}>
        Light
      </button>
    </div>
  )
}
