import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

import { Indicator } from '../components/UI/indicator'
import { Heading } from '../typography/heading'
import { LevelWrapper } from '../components/UI/levelWrapper'
import { AppButton } from '../components/UI/AppButton'

const StepFour = ( ) => {
  const navigate = useNavigate()

  const [checkedAnswer, setCheckedAnswer] = useState(null)

  const handleCheck = (answerId) => {
    setCheckedAnswer(answerId)
  }
  const options = [
    {
      id: 'variant-1',
      text: '1',
    },
    {
      id: 'variant-2',
      text: '2',
    },
    {
      id: 'variant-3',
      text: '3',
    },
    {
      id: 'variant-4',
      text: '4',
    },
    {
      id: 'variant-5',
      text: '5',
    },
  ]
  return (
    <div className="container">
      <div className="wrapper">
        <div className="emoji-quiz">
          <Indicator
            isActiveFirst=" _active"
            isActiveSecond=" _active"
            isActiveThirds=" _active"
            percent="45% и печеньки"
          />
          <div className="question">
            <Heading headingType="h2 " text="4. Занимательный вопрос" />
            <ul className="level-variants">
              {options.map((element) => (
                <LevelWrapper
                  key={element.id}
                  id={element.id}
                  labelText={element.text}
                  checked={checkedAnswer === element.id}
                  onChange={() => handleCheck(element.id)}
                />
              ))}
            </ul>
            <AppButton
              disabled={!checkedAnswer}
              id="next-btn"
              text="Далее"
            />
          </div>
        </div>
      </div>
    </div>
  )
}

export default StepFour
